mapper.select:
SELECT COUNT(1)
FROM 
 t


		t.PHONENUMBER = #{phoneNumber}


		AND t.LOGINNAME = #{loginName}

mapper.insert:

INSERT INTO TEAM (
	PHONENUMBER,
	PASSWORD
) VALUES (
	#{phoneNumber},
	#{password}
)

mapper.update:
UPDATE
TEAMNAME = #{teamName}, TEAMDESCRIPTION = #{teamDescription}, EMAIL = #{email}, ADDRESS = #{address}, UPDATEDATE = CURRENT_TIMESTAMP, CITY = ${city}, LINKMAN = #{linkman}, WEBCHAT = #{webchat}, QQ = #{qq}, OFFICIALSITE = #{officialSite}, SCALE = #{scale}, ESTABLISHDATE = #{establishDate}, BUSINESSDESC = #{businessDesc}, PRICERANGE = ${priceRange}, DEMAND = #{demand}, INFORESOURCE = ${infoResource}, DESCRIPTION = #{description}, TEAMPROVINCE = #{teamProvince}, TEAMCITY = #{teamCity}
,PHONENUMBER = #{phoneNumber}
,TEAMPHOTOURL = #{teamPhotoUrl}
TEAMID = ${teamId}

UPDATE


	DESCRIPTION = #{description},
	ESTABLISHDATE = #{establishDate},
	OFFICIALSITE = #{officialSite},
	BUSINESSDESC = #{businessDesc}


	TEAMID = ${teamId}

