TEAMID = ${teamId}

UPDATE t
t.RECOMMEND=1, t.RECOMMEND_SORT=( SELECT maxsort FROM (SELECT IFNULL(MAX(RECOMMEND_SORT) ,0) maxsort FROM TEAM WHERE RECOMMEND=1) tt)+1
t.TEAMID=#{teamId}
